require_relative("constants.rb")

# COMBINATION:
# Concatenate the sum of the verse-numbers for a chapter,
# Followed by the concatenation of the verse-numbers for a chapter.
# For all chapters.

@output = ""

@COLLECTION.each do |tuple|
  chapter_number = tuple[0]
  count_of_verses_in_chapter = tuple[1]

  sum_of_chapter_verse_numbers = (1..count_of_verses_in_chapter).to_a.reduce(0) { |sum, verse_number| sum + verse_number }

  concatenation_of_chapter_verse_numbers = (1..count_of_verses_in_chapter).to_a.reduce("") { |output, verse_number| output + verse_number.to_s }

  @output += sum_of_chapter_verse_numbers.to_s
  @output += concatenation_of_chapter_verse_numbers
end

# @output turns out to be the following number, which was confrmed to be divisible by 19 using:
